import{s as i,t as ei}from"./index.a6ae5b74.js";const ti="/assets/caphe-logo.a9f02c6e.png",si="/assets/nieh-logo.f7dfa40a.png",ni="/assets/erb-logo.a9e7ede3.png",ai="/assets/caaa-logo.b29983d6.jpg",li="/assets/urc-logo.1b4dac81.png",oi="/assets/hep-logo.f601f792.jpg",ri="/assets/dhdc-logo.2e4f5e83.png",ci="/assets/dwej-logo.94f9f8f6.png",mi="/assets/swdcbc-logo.47c64a6d.jpg",gi="/assets/sdev-logo.c2d2c0b4.png",pi="/assets/dhd-logo.f5cfe449.png",di="/assets/egle-logo.13ac16c7.png",hi="/assets/um-logo.8fb27737.jpg",ui="/assets/umd-logo.990ae555.png",vi="/assets/wsu-logo.ca6476dd.png",bi=ei('<section class="page-faq"><h1>Who we Are</h1><p><b>C</b>ommunity <b>A</b>ction to <b>P</b>romote <b>H</b>ealthy <b>E</b>nvironments (<b>CAPHE</b>) is a partnership among community based organizations, community residents, health service providers and public health researchers.</p><div class="caphe-img-container"><div class="caphe-img"><img alt="CAPHE Logo"></div></div><h2>Our Goal</h2><p>Our goal is to develop and implement components of a scientifically-based, community-led public health action plan to reduce air pollution and associated adverse health effects in Detroit and surrounding communities. CAPHE uses a community-based participatory research approach in which partners are involved in all phases of the work. This includes:</p><ol class="caphe-list-container"><li class="caphe-list-item">Defining the research problem.</li><li class="caphe-list-item">Designing and implementing the study.</li><li class="caphe-list-item">Interpreting and distributing the results.</li><li class="caphe-list-item">Deciding how results will be applied.</li><li class="caphe-list-item">Applying the results to create a public health action plan to improve health in Detroit.</li></ol><p>CAPHE has received funding from the National Institute of Environmental Health Sciences and the Fred A. and Barbara M. Erb Family Foundation.</p><div class="sponsor-img-container"><img class="sponsor-img" alt="National Institute of Environmental Health Sciences Logo"><img class="sponsor-img" alt="Erb Family Foundation Logo"></div><h2>Current Partnerships</h2><p>CAPHE builds on 20 years of community-academic research partnerships. At our inception, we drew on the work of three long-standing partnerships, each of which included community-based organizations, health practice and academic partners: </p><div class="partnerships-entire"><ul class="partnerships-container"><li class="partnerships-item"><img class="partnerships-img" alt="Community Action Against Asthma Logo"><div class="partnerships-text">Community Action Against Asthma</div></li><li class="partnerships-item"><img class="partnerships-img" alt="Detroit Community-Academic Urban Research Center Logo"><div class="partnerships-text">Detroit Community-Academic Urban Research Center</div></li><li class="partnerships-item"><img class="partnerships-img" alt="Healthy Environments Partnership Logo"><div class="partnerships-text">Healthy Environments Partnership</div></li></ul></div><p>Over the years, we have built on and extended our membership, now encompassing representatives from community based organizations, governmental institutions, and academic institutions.</p><h3>Community based organizations</h3><div class="community-partners-container"><ul class="community-partners-list"><li class="community-partners-item"><img class="community-partners-img" alt="Detroit Hispanic Development Corporation Logo"><div class="community-partners-text">Detroit Hispanic Development Corporation</div></li><li class="community-partners-item"><img class="community-partners-img" alt="Detroiters Working for Environmental Justice Logo"><div class="community-partners-text">Detroiters Working for Environmental Justice</div></li><li class="community-partners-item"><img class="community-partners-img" alt="Southwest Detroit Community Benefits Coalition Logo"><div class="community-partners-text">Southwest Detroit Community Benefits Coalition</div></li><li class="community-partners-item"><img class="community-partners-img" alt="Southwest Detroit Environmental Vision Logo"><div class="community-partners-text">Southwest Detroit Environmental Vision</div></li></ul></div><h3>Governmental institutions</h3><div class="gov-partners-container"><ul class="gov-partners-list"><li class="gov-partners-item"><img class="gov-partners-img" alt="Detroit Health Department Logo"><div class="gov-partners-text">Detroit Health Department</div></li><li class="gov-partners-item"><img class="gov-partners-img" alt="Michigan Environment, Great Lakes, and Energy Logo"><div class="gov-partners-text">Michigan Environment, Great Lakes, and Energy</div></li></ul></div><h3>Academic institutions</h3><div class="academic-partners-container"><ul class="academic-partners-list"><li class="academic-partners-item"><div class="academic-partners-text">University of Michigan</div><img class="academic-partners-img" alt="University of Michigan Logo"><ul class="academic-partners-sublist"><li class="academic-partners-sublist-item">School of Public Health</li><li class="academic-partners-sublist-item">Michigan Medicine</li><li class="academic-partners-sublist-item">Taubman College of Architecture and Urban Planning</li></ul></li><li class="academic-partners-item"><div class="academic-partners-text">University of Michigan - Dearborn</div><img class="academic-partners-img" alt="University of Michigan Dearborn Logo"></li><li class="academic-partners-item"><div class="academic-partners-text">Wayne State University</div><img class="academic-partners-img" alt="Wayne State University Logo"></li></ul></div><p>Representatives from each of these organizations comprise the CAPHE Steering Committee, with responsibility for overseeing CAPHE\u2019s day-to-day work, including conducting the basic research underlying the public health action plan, working collaboratively with others to develop the public health action plan, and working to implement prioritized components of the public health action plan.</p><h2>Our Structure</h2><p>CAPHE\u2019s structure is designed to promote collaboration and shared decision making at all levels of the CAPHE project, and to assure that Detroit residents and leadership have a significant voice in identifying and creating solutions to promote clean air for Detroit\u2019s residents. </p></section>',147);function Ci(){return(()=>{const e=bi.cloneNode(!0),v=e.firstChild,b=v.nextSibling,t=b.nextSibling,f=t.firstChild,C=f.firstChild,y=t.nextSibling,_=y.nextSibling,$=_.nextSibling,x=$.nextSibling,s=x.nextSibling,n=s.firstChild,S=n.nextSibling,D=s.nextSibling,E=D.nextSibling,a=E.nextSibling,L=a.firstChild,l=L.firstChild,A=l.firstChild,o=l.nextSibling,H=o.firstChild,w=o.nextSibling,P=w.firstChild,U=a.nextSibling,M=U.nextSibling,r=M.nextSibling,k=r.firstChild,c=k.firstChild,W=c.firstChild,m=c.nextSibling,j=m.firstChild,g=m.nextSibling,z=g.firstChild,B=g.nextSibling,F=B.firstChild,G=r.nextSibling,p=G.nextSibling,R=p.firstChild,d=R.firstChild,I=d.firstChild,N=d.nextSibling,O=N.firstChild,J=p.nextSibling,V=J.nextSibling,T=V.firstChild,h=T.firstChild,q=h.firstChild,K=q.nextSibling,u=h.nextSibling,Q=u.firstChild,X=Q.nextSibling,Y=u.nextSibling,Z=Y.firstChild,ii=Z.nextSibling;return i(C,"src",ti),i(n,"src",si),i(S,"src",ni),i(A,"src",ai),i(H,"src",li),i(P,"src",oi),i(W,"src",ri),i(j,"src",ci),i(z,"src",mi),i(F,"src",gi),i(I,"src",pi),i(O,"src",di),i(K,"src",hi),i(X,"src",ui),i(ii,"src",vi),e})()}export{Ci as default};
